public function postTag($keyword, $value, $customer) {
    $tag = array(
        'keyword' => $keyword,
        'value' => $value
    );

    $request = array(
        'timestamp' => time(),
        'token' => $this->config->get('module_pushketing_token'),
        'subscriber_id' => $customer,
        'tag' => array($tag)
    );

    $ch = curl_init($this->config->get('module_pushketing_endpoint') . '/tag');
    $payload = json_encode($request);
    curl_setopt($ch, CURLOPT_POSTFIELDS, $payload);
    $headers = array(
        'Content-Type: application/json'
    );
    curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_exec($ch);
    curl_close($ch);
}

if($this->config->get('module_pushketing_status')) {
    $cookie_name = 'pk_customer';
    if (isset($_COOKIE[$cookie_name])) {
        if ($query->row['total']) {
            $this->db->query("UPDATE " . DB_PREFIX . "cart SET pk_customer = '" . $this->db->escape($_COOKIE[$cookie_name]) . "' WHERE api_id = '" . (isset($this->session->data['api_id']) ? (int)$this->session->data['api_id'] : 0) . "' AND customer_id = '" . (int)$this->customer->getId() . "' AND session_id = '" . $this->db->escape($this->session->getId()) . "'");
        }else{
            $this->db->query("INSERT " . DB_PREFIX . "cart SET pk_customer = '" . $this->db->escape($_COOKIE[$cookie_name]) ."', api_id = '" . (isset($this->session->data['api_id']) ? (int)$this->session->data['api_id'] : 0) . "', customer_id = '" . (int)$this->customer->getId() . "', session_id = '" . $this->db->escape($this->session->getId()) . "', product_id = '" . (int)$product_id . "', recurring_id = '" . (int)$recurring_id . "', `option` = '" . $this->db->escape(json_encode($option)) . "', quantity = '" . (int)$quantity . "', date_added = NOW()");
            return;
        }
    }
}

if($this->config->get('module_pushketing_status')){
    $abandoned_query = $this->db->query("SELECT pk_customer, product_id FROM " . DB_PREFIX . "cart WHERE (api_id > '0' OR customer_id = '0') AND date_added < DATE_SUB(NOW(), INTERVAL 1 HOUR)");
    foreach ($abandoned_query->rows as $cart_row) {
        if($cart_row['pk_customer']) {
            $this->postTag('product_abandon', $cart_row['product_id'], $cart_row['pk_customer']);
        }
    }
}

if($this->config->get('module_pushketing_status')) {
    $cart_query = $this->db->query("SELECT product_id FROM " . DB_PREFIX . "cart WHERE cart_id = '" . (int)$cart_id . "' AND api_id = '" . (isset($this->session->data['api_id']) ? (int)$this->session->data['api_id'] : 0) . "' AND customer_id = '" . (int)$this->customer->getId() . "' AND session_id = '" . $this->db->escape($this->session->getId()) . "'");
    foreach ($cart_query->rows as $cart) {
        $cookie_name = 'pk_customer';
        if (isset($_COOKIE[$cookie_name])) {
            $this->postTag('product_cart_remove', $cart['product_id'], $_COOKIE[$cookie_name]);
        }
    }
}
